script:
frappe.ui.form.on('Salary Structure Assignment', {
    refresh(frm) {
        let fields_to_hide = [
            'custom_basic_salary',
            'custom_food_allowance',
            'custom_gosi_salary',
            'variable',
            'custom_housing_allowance',
            'custom_other_allowance'
        ];

        fields_to_hide.forEach(field => {
            frm.set_df_property(field, 'hidden', 1);
        });
    }
});
